import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';

import Login from "./Login";
import Register from "./Register";
import User from "./User";
import {authenticateUser} from "../functions/AuthenticateUser";
import {useEffect, useState} from "react";


const UserPage = () => {
    const [loggedIn, setLoggedIn] = useState(false);
    const [activeTab, setActiveTab] = useState('login');

    useEffect(() => {
        authenticateUser().then(r => {
            if (r.status === 200) {
                setLoggedIn(true);
                setActiveTab('user');
            } else {
                setLoggedIn(false);
                setActiveTab('login');
            }
        });
    });

    return (
        <Tabs
            activeKey={activeTab}
            onSelect={(k) => {
                if(k === "user" && loggedIn === false) {
                    return;
                } else if((k === "login" || k === "register") && loggedIn) {
                    return;
                }
                setActiveTab(k);
            }}
        >
            <Tab disabled={!loggedIn} eventKey="user" title="User">
                <User/>
            </Tab>
            <Tab disabled={loggedIn} eventKey="login" title="Log In">
                <Login/>
            </Tab>
            <Tab disabled={loggedIn} eventKey="register" title="Register">
                <Register/>
            </Tab>
        </Tabs>
    )
}

export default UserPage;
