    const [loggedIn, setLoggedIn] = useState(false);

    function getUsername() {
        try {
            return JSON.parse(localStorage.getItem('myToken')).username;
        } catch (e) {

        }
    }

    useEffect(async () => {
        console.log("!!!");
        const auth = await authenticateUser();
        if (auth === 200) setLoggedIn(true);
        else setLoggedIn(false);
    });

    return (
        <div>
            <h2>HOME PAGE</h2>
        </div>
    )

}

export default Home;
