import React from 'react';
import axios from 'axios';

export function authenticateUser(user) {
    return new Promise(function (resolve, reject) {
        axios
            .post("http://localhost:5000/authenticateUser", JSON.parse(localStorage.getItem('myToken')))
            .then(r => {
                if(r.status === 201) resolve(201);
                else reject(401);
            })
    })
}
