import {Button, Container, Form} from "react-bootstrap";
import {useState} from "react";
import axios from "axios";
import {useNavigate} from "react-router-dom";

const Login = () => {

    const navigate = useNavigate();

    const [loginEmail, changeLoginEmail] = useState('');
    const [loginPassword, changeLoginPassword] = useState('');

    const handleLoginEmail = (event) => changeLoginEmail(event.target.value);
    const handleLoginPassword = (event) => changeLoginPassword(event.target.value);

    const loginUser = (e) => {
        try {
            e.preventDefault();
        } catch {

        }

        axios
            .post('http://localhost:5000/newUser', {
                email: loginEmail,
                password: loginPassword
            })
            .then((res) => {
                localStorage.setItem('myToken', JSON.stringify(res.data));
                navigate('/');
            })
    }

    const test = () => {
        axios
            .get("http://localhost:5000/test").then(r => {
            console.log(r);
        });
    }

    return (
        <Container>
            <div>
                <br/>
                <h3>Log in</h3>
                <Form onSubmit={test}>
                    <Form.Group controlId="loginEmail">
                        <Form.Label>Email</Form.Label>
                        <Form.Control
                            type="email"
                            placeholder="Email"
                            value={loginEmail}
                            onChange={handleLoginEmail}
                        />
                    </Form.Group>
                    <Form.Group controlId="loginPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control
                            type="password"
                            placeholder="Password"
                            value={loginPassword}
                            onChange={handleLoginPassword}
                        />
                    </Form.Group>
                    <Button variant="success" type="submit">Login</Button>
                </Form>
            </div>
        </Container>
    )


}

export default Login;
